package spring.project.apirestwebflux.configurations;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.reactive.function.server.RequestPredicates;import org.springframework.web.reactive.function.server.RouterFunction;import org.springframework.web.reactive.function.server.RouterFunctions;import org.springframework.web.reactive.function.server.ServerResponse;import spring.project.apirestwebflux.handlers.ProductHandler;@Configurationpublic class RouterFunctionConfig {    @Bean    public RouterFunction<ServerResponse> routes(ProductHandler handler) {        return RouterFunctions                .route(RequestPredicates                        .GET("/api/products/select"), handler::select)                .andRoute(RequestPredicates                        .GET("/api/products/get/{id}"), handler::get)                .andRoute(RequestPredicates                        .POST("/api/products/insert"), handler::insert)                .andRoute(RequestPredicates                        .PUT("/api/products/update/{id}"), handler::update)                .andRoute(RequestPredicates                        .DELETE("/api/products/delete/{id}"), handler::delete)                .andRoute(RequestPredicates                        .POST("/api/products/upload/{id}"), handler::upload);    }}