package spring.project.apirestwebflux.documents;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import javax.validation.constraints.NotEmpty;@Document(collection = "categories")public class Category {    @Id    @NotEmpty    public String id;    public String name;    public Category() {    }    public Category(String name) {        this.name = name;    }}