package spring.project.apirestwebflux.documents;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import javax.validation.Valid;import javax.validation.constraints.NotEmpty;import javax.validation.constraints.NotNull;@Document(collection = "products")public class Product {    @Id    public String id;    @NotEmpty    public String name;    @NotNull    public Double price;    @Valid    public Category category;    public String photo;    public Product() {    }    public Product(@NotEmpty String name, @NotNull Double price, @Valid Category category) {        this.name = name;        this.price = price;        this.category = category;    }}