package spring.project.apirestwebflux.services;import org.springframework.http.codec.multipart.FilePart;import reactor.core.publisher.Flux;import reactor.core.publisher.Mono;import spring.project.apirestwebflux.documents.Product;public interface IProductService {    Flux<Product> select();    Mono<Product> get(String id);    Mono<Product> insert(Product product);    Mono<Product> update(Product product,String id);    Mono<Void> delete(String id);    Mono<Product> upload(String id, FilePart filePart);}